import React, { Component } from 'react'

import MsgList from '../MsgList';
import SearchNav from '../SearchNav';

export default class Dialog extends Component {
  
  state = {
    friends: [
      {id: '001', name: 'ÁÜäÂ§ß', introduction: 'ÂêÉÈ•≠', isFriend: true},
      {id: '002', name: 'ÁÜä‰∫å', introduction: 'Áù°Ëßâ', isFriend: true},
      {id: '003', name: 'Ë∑≥Ë∑≥', introduction: 'ÊâìË±ÜË±Ü', isFriend: false},
    ]
  }

  setState = (newState, id)=>{
    // ‰∏•Ê†ºÊ®°Âºè
    //console.log(newState, id);

    const {friends} = this.state;
    for (let i = 0; i < friends.length; i++) {
      if (friends[i].id === id) {
        this.setState({
          ['friends[' + i + '].isFriend'] : newState,
        })
        console.log(i, friends[i].isFriend)
        break;
      }
    }
  }

  addFriend = (friendName)=>{
    const {friends} = this.state;
    let newFriends = [{
      id: 1,
      name: friendName,
      introduction: 'üêñ',
      isFriend: false,
    }, ...friends];

    this.setState({
      friends: newFriends,
    })
  };

  render() {
    const {friends} = this.state;
    return (
      <div>
        <SearchNav addFriend={this.addFriend}/>
        <MsgList friends={friends} setState={this.setState}/>
      </div>
    )
  }
}
