import React, { Component } from 'react'
// import Dialog from './components/Dialog'
// import MyNavLink from './components/MyNavLink'
import MyNav from './pages/MyNav'
import Content from './pages/Content'
import SiderBar from './pages/SiderBar'

// import 'bootstrap/dist/css/bootstrap.min.css'

export default class App extends Component {

  state = {
    // 显示列表
    siderBarState: {
      showFriendList: 'friend',
      showGroupList: 'goup',
      showPersonalInfo: 'info',
      state: 'friend',

      setState: (newState)=>{
        console.log('stte:',newState)
        this.state = (newState == '' ? this.state : newState);
      }
    }
  }

  render() {
    const {siderBarState} = this.state;
    return (
      <div>
        {/* 导航 */}
        <div id='nav-box'>
          <MyNav setSiderBarContent={siderBarState.setState} />
        </div>
        {/* 列表 */}
        <div id='obj-list-box'>
          <SiderBar state={siderBarState}/>
        </div>
        {/* 内容 */}
        <div id='content-box'>
          <Content />
        </div>
        {/* 全局蒙层 */}
        <div id='max-mark'></div>
      </div>
      )
  }
}
