import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css'

export default class FriendList extends Component {
  state = {
    friendList: [
      {
        id: '01',
        name: '赵',
        gender: '男',
      },
      {
        id: '02',
        name: '钱',
        gender: '男',
      },
      {
        id: '03',
        name: '孙',
        gender: '女',
      },
      {
        id: '04',
        name: '李',
        gender: '女',
      }
    ]
  }

  openDialog = (friendId)=>{
    console.log('打开和', friendId, '的对话')
  }

  render() {
    const {friendList} = this.state;
    return (
      <div className={['container bg-success'].join(' ')}>
        {
          friendList.map((friend, index)=>{
            return (
              <div className={['row d-flex bg-primary col-lg-6'].join(' ')} key={index} onClick={()=>{return this.openDialog(friend.id)}}>
                <div>{friend.id}</div>
                <div>{friend.name}</div>
                <div>{friend.age}</div>
              </div>
            )
          })
        }
      </div>
    )
  }
}
