import React, { Component } from 'react'
import style from './index.module.css';

import SummaryInfo from '../SummaryInfo';

export default class Msg extends Component {
  changeState = (event)=>{
    console.log(event.target)

    const {target} = event;
    const {setState} = this.props;
    console.log(target.checked);
    setState(!target.checked);
  }

  render() {
    const {info} = this.props;
    return (
      <div className={[style.msg].join(' ')}>
        <div className='d-flex flex-row align-items-center'>
          <input id='check' type='checkBox' defaultChecked={info.isFriend} onChange={this.changeState}/>
          <SummaryInfo info={info}/>
          <div className='ml-auto'>
            <button type='button' className={[style.delBtn, 'btn btn-danger btn-sm mr-auto'].join(' ')} style={{}}>删除</button>
          </div>
        </div>
      </div>
    )
  }
}
