import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css'
import PropTypes from 'prop-types'

export default class DialogFooter extends Component {
  static propTypes = {
    friends: PropTypes.array.isRequired,
  }

  chooseAll = (event)=>{
    const {target} = event;
    if (target.checked){
      const chooseAllFunc = this.props.chooseAll;
      chooseAllFunc();
    }
    else{
      return;
    }
  }

  render() {
    const {friends} = this.props;
    let choosedNum = friends.filter((item)=>{
      return item.isFriend === true
    }).length;
    let total = friends.length;

    return (
      <div className='d-flex align-items-center'>
        <input type='checkBox' onChange={chooseAll} />
        <div>选中{choosedNum}&nbsp;/&nbsp;全部{total}</div>
        <div className='ml-auto'>
          <button type='button' className='btn btn-danger'>清除所有选中</button>
        </div>
      </div>
    )
  }
}
