import React, { Component } from 'react'

import MsgList from '../MsgList';
import SearchNav from '../SearchNav';

export default class Dialog extends Component {
  
  state = {
    friends: [
      {id: '001', name: 'ÁÜäÂ§ß', introduction: 'ÂêÉÈ•≠', isFriend: true},
      {id: '002', name: 'ÁÜä‰∫å', introduction: 'Áù°Ëßâ', isFriend: true},
      {id: '003', name: 'Ë∑≥Ë∑≥', introduction: 'ÊâìË±ÜË±Ü', isFriend: false},
    ]
  }
  // ËÆæÁΩÆÁä∂ÊÄÅ
  setIsFriend = (newState, id)=>{
    // ‰∏•Ê†ºÊ®°Âºè
    const {friends} = this.state;
    for (let i = 0; i < friends.length; i++) {
      if (friends[i].id === id) {
        friends[i].isFriend = newState;
        this.setState({
          friends: friends,
        })
        break;
      }
    }
  }
  // Ê∑ªÂä†Â•ΩÂèã
  addFriend = (friendName)=>{
    const {friends} = this.state;
    let newFriends = [{
      id: 1,
      name: friendName,
      introduction: 'üêñ',
      isFriend: false,
    }, ...friends];

    this.setState({
      friends: newFriends,
    })
  };
  // Âà†Èô§Â•ΩÂèã
  deleteFriend = (friendId)=>{
    const {friends} = this.state;
    let index = friends.findIndex((item)=>{
      return item.id === this.friendId
    })
    friends.slice(index, 1);
    this.setState({
      friends: friends,
    })
  }

  render() {
    const {friends} = this.state;
    return (
      <div>
        <SearchNav addFriend={this.addFriend}/>
        <MsgList friends={friends} setIsFriend={this.setIsFriend}/>
      </div>
    )
  }
}
