import React, { Component } from 'react'

import MsgList from '../MsgList';
import SearchNav from '../SearchNav';

export default class Dialog extends Component {
  
  state = {
    friends: [
      {id: '001', name: '熊大', introduction: '吃饭', isFriend: true},
      {id: '002', name: '熊二', introduction: '睡觉', isFriend: true},
      {id: '003', name: '跳跳', introduction: '打豆豆', isFriend: false},
    ]
  }

  addFriend = (newFriend)=>{
    const {friends} = this.state;
    let newFriends = [newFriend, ...friends];
    this.setState({
      friends: newFriends,
    })
  };

  render() {
    const {friends, addFriend} = this.state;
    return (
      <div>
        <SearchNav addFriend={addFriend}/>
        <MsgList friends={friends}/>
      </div>
    )
  }
}
